#include<set>
#define re register
#define maxn 100000
#define maxm 100000
#define maxblock 320
#define maxval 200000
#define mod 998244353
#define max(a,b) ((a)>=(b)?(a):(b))
#define min(a,b) ((a)<=(b)?(a):(b))

namespace cltstream{
