void work(complex* F,int n){
	if(n==1)
		return;
	complex F1[n>>1],F2[n>>1];
    for(int i=0;i<n;i+=2){
    	F1[i]=F[2*i];
        F2[i]=F[2*i+1];
	}
    work(F1,n>>1);
    work(F2,n>>1);
    complex unit(cos(2*Pi/n),sin(2*Pi/n)),tmp=1;
    for(int i=0;i<(n>>1);++i,tmp=tmp*unit){
    	complex w=F2[i+(n>>1)]*tmp;
        F[i]=F1[i]+w;
        F[i+(n>>1)]=F1[i]-w;
	}
}
