void work(complex* F,int n){
	for(int i=0;i<n;++i)
		if(i<rev[i])
			swap(F[i],F[rev[i]]);
	for(int len=2,p=1;len<=n;len<<=1,p<<=1){
		complex unit(cos(Pi/p),sin(Pi/p));
		for(int i=0;i<n;i+=len){
			complex cur(1,0);
			for(int j=i;j<i+p;++j){
				complex tmp=F[j+p]*cur;
				F[j+p]=F[j]-tmp;
				F[j]=F[j]+tmp;
				cur=cur*unit;
			}
		}
	}
}
