		sump[cur]=1LL*suma[cur]*sumb[cur]%mod;
	}
	else{
		int mid=(l+r)>>1;
		build(cur<<1,l,mid);
		build(cur<<1|1,mid+1,r);
		pushUp(cur);
	}
