		int mid=(l+r)>>1,res=0;
		pushDown(cur,mid-l+1,r-mid);
		if(L<=mid)
			res=(res+query(L,R,t,cur<<1,l,mid))%mod;
		if(R>mid)
			res=(res+query(L,R,t,cur<<1|1,mid+1,r))%mod;
		return res;
	}
}

