}

inline void pushUp(int cur){
	sumv[cur]=(sumv[cur<<1]+sumv[cur<<1|1])%mod;
	suma[cur]=(suma[cur<<1]+suma[cur<<1|1])%mod;
	sumb[cur]=(sumb[cur<<1]+sumb[cur<<1|1])%mod;
