		sumv[cur]=(sumv[cur]%mod+mod)%mod;
		suma[cur]=(suma[cur]%mod+mod)%mod;
		sumb[cur]=(sumb[cur]%mod+mod)%mod;
		sump[cur]=1LL*suma[cur]*sumb[cur]%mod;
