						--tmp1[id2[a[j]]];
						--tmp2[a[j]];
					}
					for(re int j=(id1[r]-1)*sq1+1;j<=r;++j){
						--tmp1[id2[a[j]]];
						--tmp2[a[j]];
					}
				}
				else{
					for(re int j=l;j<=r;++j){
						++tmp1[id2[a[j]]];
						++tmp2[a[j]];
					}
					for(re int j=1;;++j)
						if(x>tmp1[j])
							x-=tmp1[j];
						else{
							for(re int k=(j-1)*sq2+1;k<=j*sq2;++k)
								if(x>tmp2[k])
									x-=tmp2[k];
								else{
