						--tmp1[id2[a[j]]];
						--tmp2[a[j]];
					}
					for(re int j=(id1[r]-1)*sq1+1;j<=r;++j){
						--tmp1[id2[a[j]]];
						--tmp2[a[j]];
					}
				}
				else{
					for(re int j=l;j<=r;++j){
						++tmp1[id2[a[j]]];
						++tmp2[a[j]];
					}
