					for(re int j=id2[x]-1;j>=1&&!ans;--j)
						if(tmp1[j])
							for(re int k=j*sq2;k>=(j-1)*sq2+1&&!ans;--k)
								if(tmp2[k])
									ans=k;
					for(re int j=l;j<=r;++j){
