								if(cnt2[id1[r]-1][k]-cnt2[id1[l]][k]+tmp2[k])
									ans=k;
					for(re int j=l;j<=id1[l]*sq1;++j){
						--tmp1[id2[a[j]]];
						--tmp2[a[j]];
					}
					for(re int j=(id1[r]-1)*sq1+1;j<=r;++j){
						--tmp1[id2[a[j]]];
						--tmp2[a[j]];
					}
				}
