int S(re int x,re int y){
	if(x<=1||g[y]>x)
		return 0;
	else{
		re int id=x<=sq?id1[x]:id2[n/x];
		re int res=F[id]-fsum[y-1];
		for(re int i=y;i<=g[0]&&g[i]*g[i]<=x;++i)
			for(re int p=g[i];p*g[i]<=x;p*=g[i])
				res+=/*1*/*S(x/p,i+1)+/*2*/;
				//1处应填f(p)，2处应填f(p*g[i])
		return res;
	}
}
