int unit[2][24],rev[maxn];
int n,r;
int fac[maxn],fnv[maxn],F[maxn],G[maxn],H[maxn];

inline int cltpow(re int x,re int y){
	re int res=1;
	for(;y;x=1LL*x*x%mod,y>>=1)
		if(y&1)
			res=1LL*res*x%mod;
	return res;
