int n,m;

inline int simEuc(re int n,re int a,re int b,re int c){
	if(!n)
		return b/c;
	if(!a)
		return (n+1)*(b/c);
	if(a>=c||b>=c){
		re int res=simEuc(n,a%c,b%c,c);
		return res+n*(n+1)*(a/c)/2+(n+1)*(b/c);
	}
	else{
		re int m=(a*n+b)/c;
		return m*n-simEuc(m-1,c,c-b-1,a);
	}
}

struct node{
